import React from 'react';
import Sketch from "react-p5"

class FractalCanvas extends React.Component {
    HEIGHT=500;
    WIDTH=500;
    x1 = 500 * 0.67;
    y1 = 250 ;

    setup = (p5, canvasParentRef) => {
        p5.createCanvas(500, 500).parent(canvasParentRef); 
        // this.sierpinksi(this.x1, this.y1, 400, p5)
    };

    draw = (p5) => {
        p5.background(255);
        // const x1 = this.WIDTH * 0.67;
        // const y1 = this.HEIGHT * 0.67;
        const x2 = this.WIDTH/2;
        const y2 = this.HEIGHT * 0.33;
        const x3 = this.WIDTH * 0.33;
        // const y3 = y1;
        // p5.triangle(x1, y1, x2, y2, x3, y3)
        const sierpinksi = (x, y, size) => {
            // console.log(p5)
            if (size > 10) {
                p5.triangle(x, y, (x + size), y, (x + (size/2)), (y - (size/2)));
                sierpinksi(x, y, (size/2))
            }
        }
        sierpinksi(this.x1, this.y1, 250)
    };


    render() {
        return (
        <div>
            <h2>Henlo, I am the canvas!</h2>
            <p>I have a blue dotted border. I do not work yet.</p> 
            <Sketch
            className="fractal-canvas"
            setup={this.setup} 
            draw={this.draw}
            sierpinksi={this.sierpinksi}
            />
        </div>
        );
    }
} // end of FractalCanvas class

export default FractalCanvas;
